// App.tsx
import React, { useState, useCallback } from 'react';
import { Node, Edge, useReactFlow } from '@xyflow/react';
import { Sidebar } from './components/Sidebar';
import { ControlsPanel } from './components/ControlsPanel';
import { GraphEditor } from './components/GraphEditor';
import { computeDominatingSet, Graph } from './algorithms/dominatingSet';

type NodeData = { label: string };

export default function App() {
  const [nodes, setNodes] = useState<Node<NodeData>[]>([]);
  const [edges, setEdges] = useState<Edge[]>([]);

  // Get pan/zoom to position new nodes (optional)
  const reactFlowInstance = useReactFlow();

  // Create a node at a default position (e.g. top‐left of viewport + offset)
  const addNode = useCallback((type: string) => {
    // get current viewport
    const { x: panX, y: panY, zoom } = reactFlowInstance.getViewport();
    // position at center-ish (you can tweak these numbers)
    const position = { x: -panX + 100, y: -panY + 100 };

    const id = `${Date.now()}`;
    const newNode: Node<NodeData> = {
      id,
      type,
      position,
      data: { label: `Node ${nodes.length + 1}` },
    };
    setNodes((nds) => [...nds, newNode]);
  }, [reactFlowInstance, nodes.length]);

  const handleCompute = () => {
    const graph: Graph = {
      nodes: nodes.map((n) => ({ id: n.id })),
      edges: edges.map((e) => ({ source: e.source, target: e.target })),
    };
    const ds = computeDominatingSet(graph);
    setNodes((nds) =>
      nds.map((n) =>
        ds.has(n.id)
          ? { ...n, style: { backgroundColor: 'lightgreen' } }
          : n
      )
    );
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Pass addNode into Sidebar */}
      <Sidebar onAddNode={addNode} />

      <div style={{ flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
        <ControlsPanel onCompute={handleCompute} />

        {/* You can place your edge‐adding UI here */}

        <GraphEditor
          nodes={nodes}
          setNodes={setNodes}
          edges={edges}
          setEdges={setEdges}
        />
      </div>
    </div>
  );
}
