import React, { useState, useCallback } from 'react';
import {
  ReactFlow,
  Background,
  Controls,
  Node,
  Edge,
  OnNodesChange,
  OnEdgesChange,
  OnConnect,
  OnNodesDelete,
  OnEdgesDelete,
  applyNodeChanges,
  applyEdgeChanges,
  useReactFlow,
} from '@xyflow/react';

type NodeData = { label: string };

type GraphEditorProps = {
  nodes: Node<NodeData>[];
  setNodes: React.Dispatch<React.SetStateAction<Node<NodeData>[]>>;
  edges: Edge[];
  setEdges: React.Dispatch<React.SetStateAction<Edge[]>>;
};

export function GraphEditor({
  nodes,
  setNodes,
  edges,
  setEdges,
}: GraphEditorProps) {
  const reactFlowInstance = useReactFlow();

  // store the last copied selection
  const [copyBuffer, setCopyBuffer] = useState<{
    nodes: Node<NodeData>[];
    edges: Edge[];
  } | null>(null);

  const onNodesChange: OnNodesChange = (changes) =>
    setNodes((nds) => applyNodeChanges(changes, nds) as Node<NodeData>[]);

  const onEdgesChange: OnEdgesChange = (changes) =>
    setEdges((eds) => applyEdgeChanges(changes, eds));

  const onConnect: OnConnect = (params) =>
    setEdges((eds) => [
      ...eds,
      { ...params, id: `${params.source}-${params.target}` },
    ]);

  const onNodesDelete: OnNodesDelete = (deleted) => {
    const delIds = new Set(deleted.map((n) => n.id));
    setNodes((nds) => nds.filter((n) => !delIds.has(n.id)));
    setEdges((eds) =>
      eds.filter((e) => !delIds.has(e.source) && !delIds.has(e.target))
    );
  };

  const onEdgesDelete: OnEdgesDelete = (deleted) => {
    const delIds = new Set(deleted.map((e) => e.id));
    setEdges((eds) => eds.filter((e) => !delIds.has(e.id)));
  };

  // Copy selected elements into buffer
  const handleCopy = useCallback(() => {
    const allNodes = reactFlowInstance.getNodes();
    const allEdges = reactFlowInstance.getEdges();
    const selectedNodes = allNodes.filter((n) => n.selected);
    const selectedIds = new Set(selectedNodes.map((n) => n.id));
    const selectedEdges = allEdges.filter(
      (e) => e.selected && selectedIds.has(e.source) && selectedIds.has(e.target)
    );
    setCopyBuffer({ nodes: selectedNodes, edges: selectedEdges });
  }, [reactFlowInstance]);

  // Paste buffer with new IDs and an offset
  const handlePaste = useCallback(() => {
    if (!copyBuffer) return;
    const { nodes: bufNodes, edges: bufEdges } = copyBuffer;

    // generate new IDs and map old -> new
    const idMap = new Map<string, string>();
    bufNodes.forEach((n) => {
      idMap.set(n.id, `${n.id}-${Date.now()}`); // or use uuid
    });

    // offset so pasted items aren't directly on top
    const offset = { x: 20, y: 20 };

    // create new nodes
    const newNodes = bufNodes.map((n) => ({
      ...n,
      id: idMap.get(n.id)!,
      position: {
        x: n.position.x + offset.x,
        y: n.position.y + offset.y,
      },
      selected: false,
    }));

    // create new edges with remapped IDs
    const newEdges = bufEdges.map((e) => ({
      ...e,
      id: `${idMap.get(e.source)}-${idMap.get(e.target)}`,
      source: idMap.get(e.source)!,
      target: idMap.get(e.target)!,
      selected: false,
    }));

    // append to state
    setNodes((nds) => nds.concat(newNodes));
    setEdges((eds) => eds.concat(newEdges));
    // update clipboard so you can paste again if desired
    setCopyBuffer({ nodes: newNodes, edges: newEdges });
  }, [copyBuffer]);

  // catch key events for copy/paste
  const onKeyDown = useCallback(
    (event: React.KeyboardEvent) => {
      // Ctrl+C
      if ((event.ctrlKey || event.metaKey) && event.key === 'c') {
        event.preventDefault();
        handleCopy();
      }
      // Ctrl+V
      if ((event.ctrlKey || event.metaKey) && event.key === 'v') {
        event.preventDefault();
        handlePaste();
      }
    },
    [handleCopy, handlePaste]
  );

  // Drag/drop handlers omitted for brevity...
  const onDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };
  const onDrop = (e: React.DragEvent) => {
    // your existing drop→add-node logic
  };

  return (
    <div
      style={{ width: '100%', height: '90vh' }}
      onKeyDown={onKeyDown}
      tabIndex={0}        // make focusable so it receives key events
      onDrop={onDrop}
      onDragOver={onDragOver}
    >
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodesDelete={onNodesDelete}
        onEdgesDelete={onEdgesDelete}
        fitView
        // enable box selection by default
        selectionOnDrag
      >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
}
