// src/components/GraphEditor.tsx
import React, { useState, useCallback } from 'react';
import {
  ReactFlow,
  ReactFlowInstance,
  Background,
  Controls,
  Node,
  Edge,
  OnNodesChange,
  OnEdgesChange,
  OnConnect,
  applyNodeChanges,
  applyEdgeChanges,
} from '@xyflow/react';

type GraphEditorProps<NodeDataType extends Record<string, unknown>> = {
  nodes: Node<NodeDataType>[];
  setNodes: React.Dispatch<React.SetStateAction<Node<NodeDataType>[]>>;
  edges: Edge[];
  setEdges: React.Dispatch<React.SetStateAction<Edge[]>>;
};

export function GraphEditor<NodeDataType extends Record<string, unknown>>({
  nodes,
  setNodes,
  edges,
  setEdges,
}: GraphEditorProps<NodeDataType>) {
  // hold onto the RF instance so we can project coordinates
  const [rfInstance, setRfInstance] = useState<ReactFlowInstance | null>(null);

  const onInit = useCallback((instance: ReactFlowInstance) => {
    setRfInstance(instance);
  }, []);

  const onNodesChange: OnNodesChange = (changes) =>
    setNodes((nds) => applyNodeChanges(changes, nds) as Node<NodeDataType>[]);

  const onEdgesChange: OnEdgesChange = (changes) =>
    setEdges((eds) => applyEdgeChanges(changes, eds));

  const onConnect: OnConnect = (params) =>
    setEdges((eds) => [
      ...eds,
      { ...params, id: `${params.source}-${params.target}` },
    ]);

  const onDragOver = (event: React.DragEvent) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  };

  const onDrop = (event: React.DragEvent) => {
    event.preventDefault();
    if (!rfInstance) return;

    const reactFlowBounds = (event.currentTarget as HTMLDivElement).getBoundingClientRect();
    const type = event.dataTransfer.getData('application/reactflow');
    if (!type) return;

    // project to viewport coords
    const { x, y } = rfInstance.project({
      x: event.clientX - reactFlowBounds.left,
      y: event.clientY - reactFlowBounds.top,
    });

    const newNode: Node<NodeDataType> = {
      id: `${+new Date()}`,
      type,
      position: { x, y },
      data: { label: `New node` } as NodeDataType,
    };

    setNodes((nds) => nds.concat(newNode));
  };

  return (
    <div
      style={{ width: '100%', height: '90vh' }}
      onDrop={onDrop}
      onDragOver={onDragOver}
    >
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onInit={onInit}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        fitView
      >
        <Background />
        <Controls />
      </ReactFlow>
    </div>
  );
}
