import React, { useState } from 'react';
import { GraphEditor } from './components/GraphEditor';
import { ControlsPanel } from './components/ControlsPanel';
import { computeDominatingSet, Graph } from './algorithms/dominatingSet';

function App() {
  // State passed down to GraphEditor (could use context or global store if preferred)
  const [nodes, setNodes] = useState<{ id: string; data: any; position: any }[]>([]);
  const [edges, setEdges] = useState<{ id: string; source: string; target: string }[]>([]);

  const handleCompute = () => {
    const graph: Graph = { nodes: nodes.map(n => ({ id: n.id })), edges: edges.map(e => ({ source: e.source, target: e.target })) };
    const ds = computeDominatingSet(graph);
    // Highlight nodes in the set (e.g., by changing their style)
    setNodes(nodes.map(n =>
      ds.has(n.id) ? { ...n, style: { backgroundColor: 'lightgreen' } } : n
    ));
  };

  return (
    <>
      <ControlsPanel onCompute={handleCompute} />
      <GraphEditor 
        nodes={nodes} 
        setNodes={setNodes}
        edges={edges}
        setEdges={setEdges}
      />
    </>
  );
}
